    pkg: foundPkg ? foundPkg.packageJson : {},
    argv: process.argv.slice(2),
    flags: {},
    inferType: false,
    input: 'string',
    help: helpText,
    autoHelp: true,
    autoVersion: true,
    booleanDefault: false,
    hardRejection: true,
    allowUnknownFlags: true,
    ...options
  };

  if (options.hardRejection) {
    hardRejection();
  }

  validateOptions(options);
  let parserOptions = {
    arguments: options.input,
    ...buildParserFlags(options)
