  const missingRequiredFlags = getMissingRequiredFlags(options.flags, flags, input);

  if (missingRequiredFlags.length > 0) {
    reportMissingRequiredFlags(missingRequiredFlags);
    process.exit(2);
  }

  return {
    input,
    flags,
    unnormalizedFlags,
    pkg,
    help,
    showHelp,
    showVersion
  };
};

module.exports = meow;

